import React, { Component } from 'react';
import { Col, Row, Spinner, Badge,Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

import './App.css';

class App extends Component {
  state = {
    response: '',
    loading: '',
    country: '',
    responseToPost: '',
  };

  componentDidMount() {
    this.setState({ loading: false });
/*     this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err)); */
  }

/*   callApi = async () => {
    const response = await fetch('/api/weather');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  }; */

  handleSubmit = async e => {
    this.setState({ loading: true });
    e.preventDefault();
    const response = await fetch('api/weather', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ cityName: this.state.country }),
    });
    const body = await response.json();
    this.setState({ responseToPost: body });
    this.setState({ loading: false });
  };

render() {
    return (
      <div className="App">
        <h1><Badge color="warning">Weather React application</Badge></h1>
        <Form action="api/weather"method ="post" onSubmit={this.handleSubmit}>
          <Row class ="form">
            <Col md={6}>
            <FormGroup>
              <Label for="city">Ciudad</Label>
              <Input
                type="text"
                id="city"
                value={this.state.country}
                onChange={e => this.setState({ country: e.target.value })}
              />
            </FormGroup>
            </Col>
          </Row>

          <Button color="danger"type="submit">Search</Button>
        </Form>

        {this.state.responseToPost!=='' && !this.state.loading &&
          <>
                <p>Temperatura de {this.state.responseToPost.city}: {this.state.responseToPost.temp} celcius</p>
                <p>Descripcion: {this.state.responseToPost.desc}</p>
                <img src={this.state.responseToPost.icon_url}></img>
            </>
        }
        {this.state.loading &&
          <Spinner type="grow" color="primary" />
        }

      </div>
    );
  }
}

export default App;
