import React, { Component } from 'react';
import { Alert, Col, Row, Spinner, Badge,Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

import './App.css';

class App extends Component {
  state = {
    response: '',
    loading: '',
    country: '',
    responseToPost: '',
    error:'',
  };

  componentDidMount() {
    this.setState({ loading: false });
/*     this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err)); */
  }

/*   callApi = async () => {
    const response = await fetch('/api/weather');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  }; */

  handleSubmit = async e => {
    this.setState({ loading: true });
    e.preventDefault();
    const response = await fetch('api/weather', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ cityName: this.state.country }),
    })
    const body = await response.json();

    this.setState({ responseToPost: body });
    this.setState({ loading: false });
  };

render() {
    return (
      <div className="App">
        <h1><Badge color="warning">Weather React application</Badge></h1><br></br>
        <Form action="api/weather"method ="post" onSubmit={this.handleSubmit}>
            <FormGroup row>
              <Input
                type="text"
                id="city"
                value={this.state.country}
                placeholder="Search a city"
                onChange={e => this.setState({ country: e.target.value })}
              />
            </FormGroup>

          <Button color="danger"type="submit">Search</Button>
        </Form>
        <br></br>
        <br></br>

        {this.state.responseToPost!=='' && !this.state.loading &&
            <>
                <Alert color="success"> Temperatura de {this.state.responseToPost.city}: {this.state.responseToPost.temp} celcius</Alert>
                <Alert color="primary"> Descripcion: {this.state.responseToPost.desc}</Alert>
                <img src={this.state.responseToPost.icon_url}></img>
            </>
        }
        {this.state.loading &&
          <Spinner type="grow" color="primary" />
        }

      </div>
    );
  }
}

export default App;
