import React, { Component } from 'react';

import logo from './logo.svg';

import './App.css';

class App extends Component {
  state = {
    response: '',
    country: '',
    responseToPost: '',
  };

  componentDidMount() {
/*     this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err)); */
  }

/*   callApi = async () => {
    const response = await fetch('/api/weather');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  }; */

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('api/weather', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ cityName: this.state.country }),
    });
    const body = await response.json();

    this.setState({ responseToPost: body });
  };

render() {
    return (
      <div className="App">
        <h1>Weather React application</h1>
        <form action="api/weather"method ="post" onSubmit={this.handleSubmit}>
          <p>
            <strong>Cliudad:</strong>
          </p>
          <input
            type="text"
            value={this.state.country}
            onChange={e => this.setState({ country: e.target.value })}
          />
          <button type="submit">Submit</button>
        </form>
        <p>Temperatura de : {this.state.responseToPost.temp} celcius</p>
        <p>Descripcion: {this.state.responseToPost.desc}</p>
        <img src={this.state.responseToPost.icon_url}></img>

      </div>
    );
  }
}

export default App;
